sprites.onOverlap(SpriteKind.Player, SpriteKind.Enemy, function (sprite, otherSprite) {
    info.changeLifeBy(-1)
    otherSprite.destroy()
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.enny2, function (sprite, otherSprite) {
    info.changeLifeBy(-1)
    otherSprite.destroy()
})
scene.onOverlapTile(SpriteKind.Player, myTiles.tile7, function (sprite, location) {
    game.over(true)
})
sprites.onOverlap(SpriteKind.Player, SpriteKind.sord, function (sprite, otherSprite) {
    if (xy == 1) {
        otherSprite.y += -2
    } else if (xy == 2) {
        otherSprite.y += 2
    } else if (xy == 3) {
        otherSprite.x += -2
    } else if (xy == 4) {
        otherSprite.x += 2
    }
    pause(1)
})
sprites.onOverlap(SpriteKind.enny2, SpriteKind.Projectile, function (sprite, otherSprite) {
    if (sprites.readDataNumber(sprite, "life") <= 0) {
        sprite.destroy()
    } else {
        sprites.changeDataNumberBy(sprite, "life", -1)
        otherSprite.destroy()
        sprite.follow(mySprite, -60)
        pause(500)
        sprite.follow(mySprite, 60)
        info.changeScoreBy(1)
    }
})
